    <div class="login">
      <div v-if="inviteopen==false" class="loginCon">
        <div class="left">
          <h1>{{$t('message.免密码登录')}}</h1>
          <el-form
            :model="loginRuleForm"
            status-icon
            :rules="loginRules"
            ref="loginRuleForm"
            class="ruleForm"
          >
            <el-form-item :label="$t('message.手机号码')" prop="username">
              <div class="PhoneNumber">
                <div class="AreaCode">
                  <el-select v-model="inArealist" placeholder=" ">
                    <el-option
                      v-for="item in areaList"
                      :key="item.value"
                      :label="item.value"
                      :value="item.value"
                    >
                      <span style="float: left">{{ item.label }}</span>
                      <span style="float: right; color: #8492a6; font-size: 13px">{{ item.value }}</span>
                    </el-option>
                  </el-select>
                </div>
                <el-input
                  v-model="loginRuleForm.username"
                  onkeyup="this.value = this.value.replace(/[^\d.]/g,'');"
                  :placeholder="$t('message.请填写手机号码')"
                  :clearable="true"
                  :maxlength="11"
                  class="pohonecon"
                ></el-input>
              </div>
            </el-form-item>
            <el-form-item :label="$t('message.验证码')" prop="code">
              <div class="codeNumber">
                <el-input
                  type="text"
                  v-model="loginRuleForm.code"
                  onkeyup="this.value = this.value.replace(/[^\d.]/g,'');"
                  :maxlength="4"
                  :placeholder="$t('message.请填写验证码')"
                ></el-input>
                <i
                  class="codebtn"
                  v-if="verShow"
                  @click="getVerify(loginRuleForm.username)"
                >{{$t('message.获取验证码')}}</i>
                <i class="codebtn" v-else>{{$t('message.重新获取')}}({{ timer }})</i>
              </div>
            </el-form-item>
            <el-form-item>
              <el-button
                v-if="loginRuleForm.username != '' && loginRuleForm.code != ''"
                type="primary"
                class="submitBtn"
                :loading="onPost"
                @click="loginStatus('loginRuleForm')"
              >{{ $t("message.登录") }}/{{ $t("message.注册") }}</el-button>
              <el-button
                v-else
                type="primary"
                :class="'submitBtn isfalse'"
                :loading="onPost"
              >{{ $t("message.登录") }}/{{ $t("message.注册") }}</el-button>
            </el-form-item>
            <el-form-item>
              <div style="line-height: 22px" v-if="$i18n.locale!='kr'">
                {{ $t("message.登录") }}/{{ $t("message.注册") }}{{ $t("message.即表示同意本站的") }}
                <a
                  :href="'/'+$i18n.locale+'/about/privacy/'"
                  target="_blank"
                >《{{$t("message.隐私协议")}}》</a>
                {{$t("message.和")}}
                <a
                  :href="'/'+$i18n.locale+'/about/agreement/'"
                  target="_blank"
                >《{{$t("message.用户协议")}}》</a>
              </div>
              <div style="line-height: 22px" v-else>
                {{ $t("message.登录") }}/{{ $t("message.注册") }} 은
                <a
                  :href="'/'+$i18n.locale+'/about/privacy/'"
                  target="_blank"
                >《{{$t("message.隐私协议")}}》</a>
                {{$t("message.和")}}
                <a
                  :href="'/'+$i18n.locale+'/about/agreement/'"
                  target="_blank"
                >《{{$t("message.用户协议")}}》에 동의 함을 의미합니다.</a>
              </div>
            </el-form-item>
          </el-form>
        </div>
        <div class="right">
          <div class="rightTxt">
            <h2>{{ $t("message.欢迎回来") }}</h2>
            <p>{{ $t("message.体验快乐的挖矿旅程") }}</p>
          </div>
          <img :src="require('~/assets/loginright.png')" alt />
        </div>
      </div>
      <div class="is_showCon" v-else>
        <h2>{{ $t("message.邀请码") }}</h2>
        <p>{{ $t("message.请输入邀请码，或跳过") }}</p>

        <div class="inputcon">
          <el-input type="text" v-model="loginRuleForm.invite" :placeholder="$t('message.请输入邀请码')"></el-input>
        </div>
        <div class="inputcon">
          <el-button
            type="primary"
            :class="'conBtn first'"
            :loading="onPost"
            @click="loginsubmitForm('loginRuleForm')"
          >{{ $t("message.确认") }}</el-button>
          <el-button
            type="primary"
            :class="'conBtn skip'"
            @click="loginsubmitForm('loginRuleForm')"
          >{{ $t("message.跳过") }}</el-button>
        </div>
