<script>
const Cookie = process.client ? require("js-cookie") : undefined;
export default {
  name: "login",
  head() {
    return {
      titleTemplate: "Login In",
    };
  },
  data() {
    var checkName = (rule, value, callback) => {
      if (!value) {
        return callback(new Error(this.$t("message.请填写手机号码")));
      } else if (!/^1(3|4|5|6|7|8|9)\d{9}$/.test(value)) {
        callback(new Error(this.$t("message.手机号码有误")));
      } else {
        callback();
      }
    };
    var validatePass = (rule, value, callback) => {
      if (!value) {
        callback(new Error(this.$t("message.请填写验证码")));
      } else if (value.length < 4) {
        callback(new Error(this.$t("message.验证码有误")));
      } else {
        callback();
      }
    };
    // let user_invite = this.$cookies.get("USER_INVITE");
    return {
      loading: false,
      onPost: false, //提交状态
      timer: 60,
      verShow: true,
      is_new: false,
      inviteopen: false,
      queryinvite: false,
      areaList: [
        {
          value: "+86",
          label: "中国"
        },
        {
          value: "0082",
          label: "韩国"
        }
      ],
      inArealist: "+86", //当前区号
      loginRuleForm: {
        code: "",
        username: "",
        invite: ""
      },
      loginRules: {
        code: [{ validator: validatePass, trigger: "blur" }],
        username: [{ validator: checkName, trigger: "blur" }]
      }
    };
  },
  created() {
    if (this.$route.query.invite) {
      this.loginRuleForm.invite = this.$route.query.invite;
      this.queryinvite = true;
    }
  },
  computed: {
    // 计算属性
  },
  components: {
    // 组件的引用
  },
  methods: {
    successTip(msg) {
      this.$notify({
        type: "success",
        message: msg
      });
    },
    errorTip(msg) {
      this.$notify.error({
        message: msg
      });
    },
    getVerify(value) {
      //获取验证码
      let timestamp = new Date().getTime();
      let newtime = (timestamp / 10).toFixed(0);
      if (!value) return this.errorTip(this.$t("message.手机号码不能为空"));
      this.verShow = false;
      let auth_timer = setInterval(() => {
        this.timer--;
        if (this.timer <= 0) {
          this.verShow = true;
          this.timer = 60;
          clearInterval(auth_timer);
        }
      }, 1000);
      this.$axios
        .$get("account/verify/code/", {
          params: {
            phone: this.inArealist + value,
            action: "login",
            t: newtime
          }
        })
        .then(result => {
          if (result.status == 1) {
            if (this.queryinvite == false) {
              this.is_new = result.data.is_new;
            } else {
              this.is_new = false;
            }
            this.successTip(this.$t("message.发送成功"));
          } else {
            this.errorTip(result.msg);
            this.verShow = true;
            this.timer = 60;
          }
        });
    },
    loginStatus(value) {
      if (this.is_new == true) {
        this.inviteopen = true;
      } else {
        this.loginsubmitForm(value);
      }
    },
    loginsubmitForm(formName) {
      this.onPost = true;
      let dataRe = {
        username: this.inArealist + this.loginRuleForm.username,
        verify: this.loginRuleForm.code,
        invite: this.loginRuleForm.invite,
        t: this.$store.state.newtime
      };
      this.$axios.$post("account/login/", dataRe).then(result => {
        // 如果正确的返回了token，则将token写入cookie
        if (result.status == 1) {
          this.successTip(this.$t("message.登录成功"));
          Cookie.set("token", result.data.token);
          //写入状态管理器
          this.$store.commit("setUserinfo", result.data.profile);
          this.$store.commit("setToken", result.data.token);
          this.onPost = false; //提交状态
          this.inviteopen == false;
          if(result.data.prize==true){
           this.$router.push("/" + this.$store.state.locale + "/user/product/");
          }else{
           this.$router.push("/" + this.$store.state.locale + "/user/productlist/");
          }
        } else {
          this.errorTip(this.$t("message.账号或验证码错误"));
          this.inviteopen = false;
          this.onPost = false; //提交状态
        }
      });
    }
  }
};
</script>
